import { Injectable } from '@angular/core';
import { io } from 'socket.io-client';

@Injectable({
  providedIn: 'root',
})
export class SocketService {
  private socket: any;

  constructor() {
    // Configura la conexión al servidor de sockets
    this.socket = io('http://localhost:3000');
  }

  // Define métodos para enviar y recibir mensajes por sockets
  sendMessage(message: string) {
    this.socket.emit('chatMessage', message);
  }

  // Agrega otros métodos según tus necesidades

  // Suscríbete a eventos para recibir mensajes del servidor
  onMessageReceived(callback: (message: string) => void) {
    this.socket.on('tablero', (message: string) => {
      callback(message);
    });
  }
}
